import './App.css';
import Header from './components/Header/header.js';
import Section from './components/Section/section.js';
import ProjectCard from './components/ProjectCard/projectCard.js';

const socialLinks = [
  { id: 1, name: 'Wikipedia', url: 'https://pt.wikipedia.org/wiki/Nikola_Tesla', icon: './images/wikipedia.png' },
  { id: 2, name: 'Instagram', url: 'https://instagram.com/', icon: './images/wikipedia.png' },
  { id: 3, name: 'LinkedIn', url: 'https://linkedin.com/', icon: './images/wikipedia.png' },
];

const projectsData = [
  { id: 1, title: 'Gerador de Circuitos Quânticos', description: 'Simulação de princípios da física quântica aplicados à computação.', imageUrl: process.env.PUBLIC_URL + '/images/tesla-project1.jpg', projectUrl: '#' },
  { id: 2, title: 'Torre de Transmissão de Energia', description: 'Estudo interativo sobre a transmissão de energia sem fio.', imageUrl: process.env.PUBLIC_URL + '/images/tesla-project2.jpg', projectUrl: '#' },
  { id: 3, title: 'Analisador de Ondas Cerebrais', description: 'Visualização de padrões de ondas cerebrais (conceitual).', imageUrl: process.env.PUBLIC_URL + '/images/tesla-project3.jpg', projectUrl: '#' },
];

function App() {
  return (
    <div className="App">
      <Header/>
      <main>
        <Section
          id="profile"
          className="profile-section"
          backgroundColor="#0a192f"
        >
          <div className="profile-layout">
            <div className="profile-image-container">
              <img src={process.env.PUBLIC_URL + "./images/tesla.png"} alt="Tesla" className="profile-image" />
                <div className="profile-image-placeholder">FOTO</div>
            </div>
            <div className="profile-text">
              <h1 className="profile-name">Nikola Tesla</h1>
              <p className="brief-description">
                Inventor, engenheiro eletrotécnico e engenheiro mecânico sérvio, mais conhecido por suas contribuições ao projeto do moderno sistema de fornecimento de eletricidade em corrente alternada.
              </p>
              <div className="social-icons-container">
                {socialLinks.map(social => (
                  <a key={social.id} href={social.url} target="_blank" rel="noopener noreferrer" title={social.name}>
                    <img src={process.env.PUBLIC_URL + social.icon} alt={social.name} className="social-icon" />
                  </a>
                ))}
              </div>
            </div>
          </div>
        </Section>
      </main>
    </div>
  );
}

export default App;
